import React, { useState } from 'react';

interface LoginProps {
  onLogin: (token: string, userId: number, email: string) => void;
}

const Login: React.FC<LoginProps> = ({ onLogin }) => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    setError('');
    try {
      const res = await fetch('http://localhost:4000/login', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ email, password }),
      });
      const data = await res.json();
      if (data.token) {
        localStorage.setItem('token', data.token); // Store for API use!
        localStorage.setItem('userId', data.userId); // Store userId (for completeness)
        localStorage.setItem('email', data.email);
        onLogin(data.token, data.userId, data.email);
      } else {
        setError(data.error || 'Login failed');
      }
    } catch {
      setError('Login error');
    }
  };

  return (
    <form onSubmit={handleSubmit} className="mb-3">
      <h2 className="h5 mb-3">Login</h2>
      {error && <div className="alert alert-danger">{error}</div>}
      <input
        type="email"
        className="form-control mb-2"
        placeholder="Email"
        value={email}
        onChange={e => setEmail(e.target.value)}
        required
      />
      <input
        type="password"
        className="form-control mb-2"
        placeholder="Password"
        value={password}
        onChange={e => setPassword(e.target.value)}
        required
      />
      <button className="btn btn-success w-100" type="submit">
        Log In
      </button>
    </form>
  );
};

export default Login;
